import { Component, OnInit } from '@angular/core';
import { TaskService } from '../../services/task.service';

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css']
})
export class TaskFormComponent implements OnInit {

  constructor(
    public taskService: TaskService
   ) {}

  ngOnInit(): void {

  }

  addTask(newTitle: HTMLInputElement, newDescription: HTMLTextAreaElement) {
    console.log('adding...', newTitle.value, newDescription.value)
    this.taskService.addTask({
      title: newTitle.value,
      description: newDescription.value,
      hide:true
    });
    return false;
  }
}
<div class="card">
    <div class="card-body">
        <form (submit)="addTask(newTitle, newDescription)">
            <div class="form-group">
                <input type="text" class="form-control" placeholder="Task title" #newTitle>
            </div>
            <div class="form-group">
                <textarea cols="30" rows="10" placeholder="Task Description" class="form-control" #newDescription></textarea>
            </div>
            <button class="btn btn-primary btn-block">
                Send
            </button>
        </form>
    </div>
</div>
