  }
}
<div class="card">
    <div class="card-body">
        <form (submit)="addTask()">
            <div class="form-group">
                <input type="text" class="form-control" placeholder="Task title">
            </div>
            <div class="form-group">
                <textarea cols="30" rows="10" placeholder="Task Description" class="form-control"></textarea>
